##################################################### helper functions ##############################################################

#####################################################################################################################################
# this function erases the hero drawing
CLEAR_HERO:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
	la $a0, hero_pos			# $a0 stores the address of the hero position variable
	lw $a0, 0($a0)				# $a0 stores the word in that address(stores the hero_pos variable value)
	add $a0, $a0, $s0			# $a0 stores $a0+$s0(address of the hero's position)
	addi $a1, $0, 10			# $a1 stores 10(length of column)
	add $a2, $s4, $0			# $a2 stores black color code
	addi $a3, $0, 512			# $a3 stores the increment to the next address (512->fill columns)
	
	jal FILL				# clear column1
	addi $a0, $a0, 4			# move to column2
	jal FILL				# clear column2
	addi $a0, $a0, 4			# move to column3
	jal FILL				# clear column3
	addi $a0, $a0, 4			# move to column4
	jal FILL				# clear column4
	addi $a0, $a0, 4			# move to column5
	jal FILL				# clear column5
	addi $a0, $a0, 4			# move to column6
	jal FILL				# clear column6
	addi $a0, $a0, 4			# move to column7
	jal FILL				# clear column7
	addi $a0, $a0, 4			# move to column8
	jal FILL				# clear column8
	addi $a0, $a0, 4			# move to column9
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address	
#####################################################################################################################################

#####################################################################################################################################
# this function draws the hero colliding
COLLIDE:
	add $t6, $0, $ra			# $t9 stores the return address in the link register
	la $a0, hero_pos
	lw $a0, 0($a0)
	add $a0, $a0, $s0
	add $a1, $s3, $0			# $a1 stores blue color code(hero's clothes/eyes)
	add $a2, $s1, $0			# $a2 stores red color code(to indicate damage)
	la $t0, hero_direction			# $t0 stores address of hero_direction
	lw $t0, 0($t0)				# $t0 stores value of hero_direction
	beq $t0, $0, COLLIDE_LEFT		# if hero_direction == 0, collide to the left
	
	COLLIDE_RIGHT:				# else collide to the right
	jal DRAW_CHARACTER_RIGHT		# draw hero colliding to the right
	j CLEAR_COLLIDE				# clear the collision
	
COLLIDE_LEFT:
	jal DRAW_CHARACTER_LEFT			# draw hero colliding to the left
	
CLEAR_COLLIDE:
	li $a0, 500				# Wait (500 milliseconds)
	add $v0, $0, 32
	syscall
	jal CLEAR_HERO				# clear hero colliding
	
ERASE_HEART:	
	la $t0, hearts				# $t0 stores address of variable hearts
	lw $a0, 0($t0)				# $a0 stores value of variable hearts
	subi $a0, $a0, 1			# $a0--
	sw $a0, 0($t0)				# decrement variable hearts
	addi $t1, $0, 40			# $t1 stores 40
	mult $a0, $t1				# hearts * 40
	mflo $a0				# $a0 = hearts * 40
	addi $a0, $a0, 28972			
	add $a0, $a0, $s0			# $a0 stores address of required heart
	add $a2, $s6, $0			# $a2 store grey color code
	jal DRAW_HEART				# draw grey heart
	
	add $ra, $0, $t6			# restore the return address from the link register
	jr $ra					# jump to the return address
#####################################################################################################################################

#####################################################################################################################################
# this function draws the hero jumping one step
JUMP_STEP:
	add $t6, $0, $ra			# $t6 stores the return address in the link register
	
	addi $a0, $0, 10			# wait 10 milliseconds
	addi $v0, $0, 32
	syscall
	
	jal CLEAR_HERO				# clear hero
	
	la $t0, hero_pos			# $t0 stores the address of the hero_pos
	lw $a0, 0($t0)				# $a0 stores value of hero_pos
	subi $a0, $a0, 512			# move one row up
	sw $a0, 0($t0)				# update hero_pos
	
	add $a0, $a0, $s0			# $a0 stores hero's position
	add $a1, $s3, $0			# $a1 stores blue color code(hero's clothes/eyes)
	add $a2, $s5, $0			# $a2 stores skin tone color code
	
	la $t0, hero_direction			# $t0 stores the address of the hero_direction
	lw $t0, 0($t0)				# $t0 stores value of hero_direction
	beq $t0, $0, JUMP_STEP_LEFT		# if hero_direction == 0, jump one step facing left
	
JUMP_STEP_RIGHT:				# else jump one step facing right
	jal DRAW_CHARACTER_RIGHT		
	j FINISH_JUMP_STEP
	
JUMP_STEP_LEFT:
	jal DRAW_CHARACTER_LEFT
	
FINISH_JUMP_STEP:
	add $ra, $0, $t6			# restore the return address from the link register
	jr $ra					# jump to the return address
	
#####################################################################################################################################

#####################################################################################################################################
# this function draws the hero falling one step
FALL_STEP:
	add $t6, $0, $ra			# $t6 stores the return address in the link register
	
	addi $a0, $0, 10			# wait 10 milliseconds
	addi $v0, $0, 32
	syscall
	
	jal CLEAR_HERO				# clear hero
	
	la $t0, hero_pos			# $t0 stores the address of the hero_pos
	lw $a0, 0($t0)				# $a0 stores value of hero_pos
	addi $a0, $a0, 512			# move one row down
	sw $a0, 0($t0)				# update hero_pos
	
	add $a0, $a0, $s0			# $a0 stores hero's position
	add $a1, $s3, $0			# $a1 stores blue color code(hero's clothes/eyes)
	add $a2, $s5, $0			# $a2 stores skin tone color code
	
	la $t0, hero_direction			# $t0 stores the address of the hero_direction
	lw $t0, 0($t0)				# $t0 stores value of hero_direction
	beq $t0, $0, FALL_STEP_LEFT		# if hero_direction == 0, fall one step facing left
	
FALL_STEP_RIGHT:				# else fall one step facing right
	jal DRAW_CHARACTER_RIGHT		
	j FINISH_FALL_STEP
	
FALL_STEP_LEFT:
	jal DRAW_CHARACTER_LEFT
	
FINISH_FALL_STEP:
	add $ra, $0, $t6			# restore the return address from the link register
	jr $ra					# jump to the return address
#####################################################################################################################################

#####################################################################################################################################
# This function fills a row/column with a color based on the given arguements:
# $a0: row/column start address
# $a1: length of row/column
# $a2: color of row/column
# $a3: 4 -> fill rows, 512 -> fill columns
FILL:
	add $t9, $0, $ra			# $t9 stores the return address in the link register
	add $t0, $0, $a0			# $t0 stores $a0(row/column start address)
	add $t1, $0, $a1			# $t1 stores $a1(length of row/column)
	
START_FILL_LOOP:
	sw $a2, 0($t0)				# fill address $t0 with the color $a2
	add $t0, $t0, $a3			# increment addresss $t0 by $a3(next address)
	subi $t1, $t1, 1			# decrement $t1 by 1
	bne $t1, $0, START_FILL_LOOP		# check loop condition($t1 != 0)

END_FILL_LOOP:
	add $ra, $0, $t9			# restore the return address from the link register
	jr $ra					# jump to the return address
####################################################################################################################################

####################################################################################################################################
# This function draws horizontal padding(left/right) for the health section filling 7 rows with length 26 with blue
HORIZONTAL_PADDING:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
						# $a0(row start address is already set)
	addi $a1, $0, 26			# $a1 stores 26(length of row)
						# $a2(color code is already set to blue)
	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
	add $t2, $a0, $0			# $t2 stores the start address $a0 because we're gonna need to restore it
	
	jal FILL				# FILL row1
	addi $a0, $a0, 512			# move to row2
	jal FILL				# FILL row2
	addi $a0, $a0, 512			# move to row3
	jal FILL				# FILL row3
	addi $a0, $a0, 512			# move to row4
	jal FILL				# FILL row4
	addi $a0, $a0, 512			# move to row5
	jal FILL				# FILL row5
	addi $a0, $a0, 512			# move to row6
	jal FILL				# FILL row6
	addi $a0, $a0, 512			# move to row7
	jal FILL				# FILL row7
	
	addi $a0, $t2, 104			# restore address $a0 and move it 26*4 pixels(26 columns)
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address
#################################################################################################################################

#################################################################################################################################
# This function draws an H
DRAW_H:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
						# $a0(row start address is already set)
	addi $a1, $0, 3				# $a1 stores 3(length of row)	
						# $a2(color code is already set to blue)
	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
	add $t2, $a0, $0			# $t2 stores the start address $a0 because we're gonna need to restore it
	
	addi $a0, $a0, 4			# move one column to the right
	
	jal FILL				# FILL row1
	addi $a0, $a0, 512			# move to row2
	jal FILL				# FILL row2
	addi $a0, $a0, 512			# move to row3
	jal FILL				# FILL row3
	addi $a0, $a0, 512			# move to row4
		
	addi $a0, $a0, 512			# move to row5 (leave row4 black)
	
	jal FILL				# FILL row5
	addi $a0, $a0, 512			# move to row6
	jal FILL				# FILL row6
	addi $a0, $a0, 512			# move to row7
	jal FILL				# FILL row7
	
	addi $a0, $t2, 20			# restore address $a0 and move it 5*4 pixels(5 columns) to be used by the next function
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address
#################################################################################################################################

#################################################################################################################################
# This function draws the normal padding of 3 columns
PADDING:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
						# $a0(row start address is already set)
	addi $a1, $0, 7				# $a1 stores 3(length of column)
						# $a2(color code is already set to blue)
	addi $a3, $0, 512			# $a3 stores the increment to the next address (512 -> fill columns)

	jal FILL				# FILL column1
	addi $a0, $a0, 4			# move to column2
	jal FILL				# FILL column2
	addi $a0, $a0, 4			# move to column3
	jal FILL				# FILL column3
	
	addi $a0, $a0, 4			# move to next column used by the next function
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address
#################################################################################################################################

#################################################################################################################################
# This function draws a heart(red/grey) based on the argueent $a2
DRAW_HEART:
	add $t8, $0, $ra			# $t8 stores the return address in the link register

	add $t2, $a0, $0			# $t2 stores the start address $a0 because we're gonna need to restore it
	
	# the next lines fill the blue pixels in the first row of the heart
	sw $s3, 0($a0)				
	addi $a0, $a0, 12
	sw $s3, 0($a0)
	addi $a0, $a0, 12
	sw $s3, 0($a0)
	
	addi $a0, $t2, 512			# move to row2
	addi $a0, $a0, 4			# move a column to the right
	
	addi $a1, $0, 5				# $a1 stores 5(length of row)
						# $a2(color code is already set to red/grey)
	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
	jal FILL				# FILL row2
	sw $s4, 8($a0)				# fill the middle with black
	
	addi $a0, $a0, 512			# move to row3
	jal FILL				# FILL row3
	addi $a0, $a0, 512			# move to row4
	jal FILL				# FILL row4
	
	addi $a0, $t2, 2048			# move to row5
	# the next lines fill row5
	sw $s3, 0($a0)
	sw $a2, 8($a0)
	sw $a2, 12($a0)
	sw $a2, 16($a0)
	sw $s3, 24($a0)
	
	addi $a0, $a0, 512			# move to row6
	# the next lines fill row6
	sw $s3, 0($a0)
	sw $s3, 4($a0)
	sw $a2, 12($a0)
	sw $s3, 20($a0)
	sw $s3, 24($a0)
	
	addi $a0, $a0, 512			# move to row7
	
	addi $a1, $0, 7				# $a1 stores 7(length of row)
	add $a2, $s3, $0			# set $a2(color code) to blue
	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
	jal FILL				# FILL row7
	sw $s4, 12($a0)				# fill middle with black
	
	addi $a0, $t2, 28			# restore address $a0 and move it 7*4 pixels(7 columns) to be used by the next function

	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address
#################################################################################################################################

#################################################################################################################################
# this function draws the three platforms
DRAW_PLATFORMS:
	add $t8, $0, $ra			# $t8 stores the return address in the link register

	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
DRAW_PLATFORM1:
	li $a0, PLATFORM1_ADDRESS		# $a0 stores PLATFORM1_ADDRESS
	addi $a1, $0, 9				# $a1 stores 9(length of row/platform)
	add $a2, $s1, $0			# $a2 stores red color code
	jal FILL				# draw the red part of platform1
	
	addi $a0, $a0, 36			# moves to the green part
	addi $a1, $0, 39			# $a1 stores 39(length of row/platform)
	add $a2, $s2, $0			# $a2 stores green color code
	jal FILL				# draw the green part of platform1
	
DRAW_PLATFORM2:
	li $a0, PLATFORM2_ADDRESS		# $a0 stores PLATFORM2_ADDRESS
	addi $a1, $0, 40			# $a1 stores 38(length of row/platform)
	add $a2, $s2, $0			# $a2 stores green color code
	jal FILL				# draws the green part of platform2
	
	addi $a0, $a0, 160			# moves to the red part
	addi $a1, $0, 9				# $a1 stores 9(length of row/platform)
	add $a2, $s1, $0			# $a2 stores green color code
	jal FILL				# draw the red part of platform2

DRAW_PLATFORM3:
	li $a0, PLATFORM3_ADDRESS		# $a0 stores PLATFORM3_ADDRESS
	addi $a1, $0, 39			# $a1 stores 39(length of row/platform)
	add $a2, $s2, $0			# $a2 stores green color code
	jal FILL
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra	
#################################################################################################################################

#################################################################################################################################
# this function draws the frame of the gate(grey/blue) based on arguement $a2 
DRAW_GATE:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
	li $a0, GATE_ADDRESS			# $a0 set later
	add $t2, $a0, $0			# $t2 stores the start address $a0 because we're gonna need to restore it
	
	# the next lines fill the grey pixels in column1
	addi $a0, $a0, 1536			# move 3 rows down
	addi $a1, $0, 5				# $a1 stores 5(length of column)
						# $a2 already set to grey
	addi $a3, $0, 512			# $a3 stores the increment to the next address (512->fill columns)
	
	jal FILL				# FILL column1
	
	addi $a0, $t2, 4			# move to column2
	
	# the next lines fill the grey pixels in column2
	addi $a0, $a0, 512			# move 1 rows down
	addi $a1, $0, 2				# $a1 stores 2(length of column)
						# $a2 and $a3 already set
	
	jal FILL				# FILL first part of column2
	
	addi $a0, $a0, 3584			# move 7 rows down
	
	jal FILL				# FILL second part of column2
	
	addi $a0, $t2, 8			# move to column3
	
	# the next lines fill the rows(columns 3 4 5)
	
	# fill top part
	sw $a2, 0($a0)
	sw $a2, 4($a0)
	sw $a2, 8($a0)
	
	addi $a0, $a0, 5120 # move to bottom part
	
	# fill bottom part
	sw $a2, 0($a0)
	sw $a2, 4($a0)
	sw $a2, 8($a0)
	
	addi $a0, $t2, 20			# move to column6
	
	# the next lines fill the grey pixels in column6
	addi $a0, $a0, 512			# move 1 rows down
	addi $a1, $0, 2				# $a1 stores 2(length of column)
						# $a2 and $a3 already set
	
	jal FILL				# FILL first part of column2
	
	addi $a0, $a0, 3584			# move 7 rows down
	
	jal FILL				# FILL second part of column2
	
	addi $a0, $t2, 24			# move to column3
	
	# the next lines fill the grey pixels in column7
	addi $a0, $a0, 1536			# move 3 rows down
	addi $a1, $0, 5				# $a1 stores 5(length of column)
						# $a2 already set to grey
	addi $a3, $0, 512			# $a3 stores the increment to the next address (512->fill columns)
	
	jal FILL				# FILL column1
	
	addi $a0, $t2, 4			# move to column2
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address
