#################################################################################################################################
# this function draws a character facing the right side
# $a0: characters position
# $a1: characters clothes/eyes color
# $a2: characters skin color
DRAW_CHARACTER_RIGHT:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
	add $t2, $a0, $0			# $t2 stores the start address $a0 because we're gonna need to restore it
	add $t3, $a1, $0			# $t3 stores the scharacters clothes/eyes color because it's going to be overwritten by other functions
	add $t4, $a2, $0			# $t4 stores the scharacters skin color because it's going to be overwritten by other functions
	
	DRAW_HEAD_RIGHT:
	addi $a0, $a0, 4			# move one column to the right
	addi $a1, $0, 4				# $a1 stores 4(length of row)
						# $a2 already set to skin color
	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
	jal FILL				# FILL row1
	addi $a0, $a0, 512			# move to row2
	
	jal FILL				# FILL row2
	sw $t3, 4($a0)				# draw left eye
	sw $t3, 12($a0)				# draw right eye
	
	addi $a0, $a0, 512			# move to row3
	jal FILL				# FILL row3
	
	DRAW_GUN_RIGHT:
	sw $s7, 20($a0)				# draw scope
	
	addi $a0, $t2, 1536			# move to row4
	addi $a1, $0, 8				# $a1 stores 8(length of row)
	add $a2, $s6, $0			# $a2 stores grey color code
						# $a3 already set to 4
					
	jal FILL				# FILL row4
	
	addi $t2, $t2, 2048
	add $a0, $t2, $0			# move to row5
	
	DRAW_UPPER_BODY_RIGHT:
	addi $a1, $0, 4				# $a1 stores 4(length of row)
	
	sw $s7, 0($a0)				# draw sleeve in row5
	sw $s7, 20($a0)				# draw sleeve in row5
	sw $t4, 24($a0)				# draw hand in row5
	
	addi $a0, $a0, 4			# move one column to the right
	add $a2, $t3, $0			# $a2 stores clothes color
						# $a1 and $a3 already set to 4
					
	jal FILL				# draw shirt in row5
	
	addi $a0, $t2, 512			# move to row6
	
	add $a2, $s7, $0			# $a2 store dark grey color code
	
	jal FILL				# draw sleeves in row6
	
	sw $t4, 16($a0)				# draw hand in row6
	
	addi $a0, $t2, 1024			# move to row7
	
	addi $a0, $a0, 4			# move one column to the right
	add $a2, $t3, $0			# $a2 store clothes color code
	
	jal FILL				# draw clothes in row7
	
	addi $t2, $t2, 1536			# move to row8
	add $a0, $t2, $0			# move to row8
	
	DRAW_LEGS_RIGHT:
	addi $a0, $a0, 4			# move 1 columns to the right
	addi $a1, $0, 3				# $a1 stores 3(length of column)
	add $a2, $s7, $0			# $a2 stores dark grey color code
	addi $a3, $0, 512			# $a3 stores the increment to the next address (512->fill columns)
	
	jal FILL				# draw first leg
	
	addi $a0, $a0, 12			# move 3 columns to the right
	
	jal FILL				# draw second leg
	
	addi $a0, $t2, 1024			# move to last row
	
	sw $s7, 8($a0)				# complete feet
	sw $s7, 20($a0)				# complete feet
	
	add $ra, $0, $t8			# restore the return address from the link register
	jr $ra					# jump to the return address
