#################################################################################################################################
# this function draws a character facing the left side
# $a0: characters position
# $a1: characters clothes/eyes color
# $a2: characters skin color
DRAW_CHARACTER_LEFT:
	add $t8, $0, $ra			# $t8 stores the return address in the link register
	
	add $t2, $a0, $0			# $t2 stores the start address $a0 because we're gonna need to restore it
	add $t3, $a1, $0			# $t3 stores the scharacters clothes/eyes color because it's going to be overwritten by other functions
	add $t4, $a2, $0			# $t4 stores the scharacters skin color because it's going to be overwritten by other functions
	
	DRAW_HEAD_LEFT:
	addi $a0, $a0, 12			# move three column to the right
	addi $a1, $0, 4				# $a1 stores 4(length of row)
						# $a2 already set to skin color
	addi $a3, $0, 4				# $a3 stores the increment to the next address (4->fill rows)
	
	jal FILL				# FILL row1
	addi $a0, $a0, 512			# move to row2
	
	jal FILL				# FILL row2
	sw $t3, 0($a0)				# draw left eye
	sw $t3, 8($a0)				# draw right eye
	
	addi $a0, $a0, 512			# move to row3
	jal FILL				# FILL row3
	
	DRAW_GUN_LEFT:
	sw $s7, -8($a0)				# draw scope
	
	addi $a0, $t2, 1536			# move to row4
	addi $a1, $0, 8				# $a1 stores 8(length of row)
	add $a2, $s6, $0			# $a2 stores grey color code
