#################################################################################################################################
# This function kills the hero from colliding with a villian
VILLIAN_KILL:
	la $t0, hearts				# $t0 stores address of hearts
	lw $t0, 0($t0)				# $t0 stores value of hearts
	beq $t0, $0, END_PROGRAM		# if hearts==0 -> DIE
						# else erase one more heart
	la $a0, hero_pos			# $a0 stores address of hero_pos
	lw $a0, 0($a0)				# $a0 stores value of hero_pos
	add $a0, $a0, $s0			# $a0 stores heros position
	jal COLLIDE				# collide again(erase a heart)
	j VILLIAN_KILL				# repeat until no more hearts
#################################################################################################################################

#################################################################################################################################
# takes $t5 as an arguement to the number of steps to fall, then kills the hero from colliding with a villain
FALL_TO_DEATH:
	beq $t5, $0, VILLIAN_KILL
	jal FALL_STEP
	subi $t5, $t5, 1
	j FALL_TO_DEATH
