respond_to_w:
							
	jal CLEAR_HERO				# clear hero
	
	la $t0, hero_pos			# $t0 stores the address of the hero_pos
	lw $a0, 0($t0)				# $a0 stores value of hero_pos
	add $a0, $a0, $s0			# $a0 stores $t0+$s0(address of the hero's position)
	addi $a0, $a0, 512			# move one row down(bending down)
	add $a1, $s3, $0			# $a1 stores blue color code(hero's clothes/eyes)
	add $a2, $s5, $0			# $a2 stores skin tone color code

	la $t0, hero_direction			# $t0 stores address of hero_direction variable
	lw $t1, 0($t0)				# $t1 stores value of hero_direction variable
	beq $t1, $0, BEND_FACING_LEFT		# if hero_direction  == 0, hero facing left, so bend facing left
						
BEND_FACING_RIGHT:				# else bend facing right
	jal DRAW_CHARACTER_RIGHT		# draw character facing right
	j CONTINUE_BEND
	
BEND_FACING_LEFT:			
	jal DRAW_CHARACTER_LEFT			# draw character facing left
	
CONTINUE_BEND:
	la $t0, hero_pos			# $t0 stores the address of the hero_pos
	lw $a0, 0($t0)				# $a0 stores value of hero_pos
	add $a0, $a0, $s0			# $a0 stores $t0+$s0(address of the hero's position)
	addi $a0, $a0, 4608			# move 9 rows dowm
	
	# fill missing feet
	sw $s7, 8($a0)
	sw $s7, 20($a0)
	
CHECK_IF_DOUBLE:				# check if it's a double jump
	la $t3, jump_status			# $t3 stores address of jump_status
	lw $t3, 0($t3)				# $t3 stores value of jump_status
	beq $t3, $0, START_JUMP			# if jump_status==0, then it's a normal jump
	
START_DOUBLE_JUMP:				# else start a double jump
	
	# cover space back with black
	addi $a0, $a0, 512			# move one row down
	sw $s4, 4($a0)
	sw $s4, 8($a0)
	sw $s4, 12($a0)
	sw $s4, 16($a0)
	sw $s4, 20($a0)
	sw $s4, 24($a0)
	
	addi $a0, $0, 200			# wait 200 milliseconds
	addi $v0, $0, 32
	syscall
	
	# jump 2 steps
	jal JUMP_STEP				
	jal JUMP_STEP
	
	la $t0, double_jump_status		# $t0 stores address of double_jump_status
	addi $t1, $0, 10			# $t1 stores 10
	sw $t1, 0($t0)				# update double_jump_status = 10
	
	la $t0, jump_status			# $t0 stores address of jump_status
	add $t1, $0, $0				# $t1 stores 0
	sw $t1, 0($t0)				# update jump_status = 0

	la $t0, hero_yaxis			# $t0 stores address of hero_yaxis
	lw $t1, 0($t0)				# $t1 stores value of hero_yaxis
	addi $t1, $t1, 1			# $t1++
	sw $t1, 0($t0)				# update hero_yaxis++
	
	# jump 5 more steps
	jal JUMP_STEP
	jal JUMP_STEP
	jal JUMP_STEP
	jal JUMP_STEP
	jal JUMP_STEP
	
	jal DRAW_PLATFORMS			# refill the platforms
	
	la $t0, hero_xaxis			# $t0 stores address of hero_xaxis
	lw $t0, 0($t0)				# $t0 stores value of hero_xaxis
	la $t1, hero_yaxis			# $t1 stores address of hero_yaxis
	lw $t1, 0($t1)				# $t1 stores value of hero_yaxis
	addi $t2, $0, 2				# $t2 stores 2
	bne $t0, $t2, responded_to_w		# if x!=2, we're done
	bne $t1, $t2, responded_to_w		# if y!=2, we're done
	
VILLAIN1_JUMP_COLIDE:				# else if (x,y) = (2,2) collide with villian1
	la $a0, hero_pos			# $a0 stores address of hero_pos
	lw $a0, 0($a0)				# $a0 stores value of hero_pos
	add $a0, $a0, $s0			# $a0 stores the hero's position
	jal COLLIDE				# collide with villian1
	
VILLIAN1_JUMP_KILL:				# erase remaining hearts
	la $t0, hearts				# $t0 stores address of hearts
	lw $t0, 0($t0)				# $t0 stores value of hearts
	beq $t0, $0, END_PROGRAM		# if hearts==0 -> DIE
						# else erase one more heart
	la $a0, hero_pos			# $a0 stores address of hero_pos
	lw $a0, 0($a0)				# $a0 stores value of hero_pos
	add $a0, $a0, $s0			# $a0 stores heros position
	jal COLLIDE				# collide again(erase a heart)
	j VILLIAN1_JUMP_KILL			# repeat
	
	
START_JUMP:					# start normal jump

	# cover lower platform back with green
	addi $a0, $a0, 512			# move one row down
	sw $s2, 4($a0)
	sw $s2, 8($a0)
	sw $s2, 12($a0)
	sw $s2, 16($a0)
	sw $s2, 20($a0)
	sw $s2, 24($a0)
	
	addi $a0, $0, 200			# wait 200 milliseconds
	addi $v0, $0, 32
	syscall
	
	# jump 2 steps
	jal JUMP_STEP				
	jal JUMP_STEP
	
	# check if allowed to continue the jump
	la $t0, hero_xaxis			# $t0 stores the address of hero_xaxis
	lw $t0, 0($t0)				# $t0 stores the value of hero_xaxis
	la $t1, hero_yaxis			# $t1 stores the address of hero_yaxis
	lw $t1, 0($t1)				# $t1 stores the value of hero_yaxis
	addi $t2, $0, 6				# $t2 stores 3
	
	beq $t1, $t2, UP_FIRE_COLLIDE		# if hero_yaxis == 3, collide with upper fire
	addi $t2, $0, 1				# $t2 stores 1
	bne $t0, $t2, CONTINUE_JUMP		# if hero_xaxis != 1, continue the jump
	bne $t1, $0, CONTINUE_JUMP		# if hero_yaxis != 0, continue the jump
	
UP_FIRE_COLLIDE:				# else if (x,y) = (1,0) colide with villian1 fire

	la $t0, hero_pos			# $t0 stores address of hero_pos
	lw $a0, 0($t0)				# $a0 stores value of hero_pos
	add $a0, $a0, $s0			# $a0 stores $a0+$s0(address of the hero's position)
	jal COLLIDE 				# collide with fire
	
	la $t0, hero_pos			# $t0 stores address of hero_pos
	lw $a0, 0($t0)				# $a0 stores value of hero_pos
	addi $a0, $a0, 1024			# move 2 rows down
	sw $a0, 0($t0)				# update hero_pos to original position
	add $a0, $a0, $s0			# $a0 stores $a0+$s0(address of the hero's position)
	add $a1, $s3, $0			# $a1 stores blue color code
	add $a2, $s5, $0			# $a2 stores skin tone color code

	la $t0, hero_direction			# $t0 stores address of hero_direction
	lw $t0, 0($t0)				# $t0 stores value of hero_direction
	beq $t0, $0, AFTER_UP_COLLISION_LEFT	# if hero_direction == 0, redraw hero after collision facing left		
	
	AFTER_UP_COLLISION_RIGHT:		# else redraw hero after collision facing right
	jal DRAW_CHARACTER_RIGHT		# draw hero facing right
	j FINISH_UP_FIRE_COLLIDE
	
	AFTER_UP_COLLISION_LEFT	:
	jal DRAW_CHARACTER_LEFT			# draw hero facing left
	
FINISH_UP_FIRE_COLLIDE:
	j responded_to_w
	
CONTINUE_JUMP:

	la $t0, jump_status			# $t0 stores address of jump_status
	addi $t1, $0, 10			# $t1 stores 10
	sw $t1, 0($t0)				# update jump_status=10

	la $t0, hero_yaxis			# $t0 stores address of hero_yaxis
	lw $t1, 0($t0)				# $t1 stores value of hero_yaxis
	addi $t1, $t1, 1			# $t1++
	sw $t1, 0($t0)				# update hero_yaxis++
	
	# jump 5 more steps
	jal JUMP_STEP
	jal JUMP_STEP
	jal JUMP_STEP
	jal JUMP_STEP
	jal JUMP_STEP
		
	
responded_to_w:	
	la $t0, bullet_move			# $t0 stores address of bullet_move
	sw $0, 0($t0)				# bullet_move = 0(end timer)
	j START_GAME				# Job Done! Check for other inputs
