	addi $t2, $0, 5				# $t2 stores 2
	bne $t1, $t2, CHECK_FALL_RIGHT 		# if y != 5 -> check if fall right
	j VILLIAN2_RIGHT_COLLIDE_UP	
	
CHECK_FALL_RIGHT:				# check if falling right from gravity
	addi $t2, $0, 5				# $t2 stores 5
	ble $t0, $t2, CONTINUE_MOVE_RIGHT	# if x <= 5 -> move to the right normally
	addi $t2, $0, 2				# $t2 stores 2
	bne $t1, $t2, CONTINUE_MOVE_RIGHT	# if y != 2 -> move to the right normally

